package com.example.somya.savoir;import android.content.Context;import android.content.Intent;import android.support.constraint.ConstraintLayout;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.LinearLayoutManager;import android.support.v7.widget.RecyclerView;import android.support.v7.widget.Toolbar;import android.view.View;import android.widget.TextView;import com.firebase.ui.database.FirebaseRecyclerAdapter;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.squareup.picasso.Picasso;import de.hdodenhof.circleimageview.CircleImageView;public class AllUsers extends AppCompatActivity {    private Toolbar mToolBar;    private RecyclerView mUsersList;    private DatabaseReference mUsersDatabase;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_all_users);        mToolBar = (Toolbar)findViewById(R.id.all_users_toolbar);        setSupportActionBar(mToolBar);        getSupportActionBar().setTitle("All Users");        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        mUsersDatabase = FirebaseDatabase.getInstance().getReference().child("Users");        mUsersList = (RecyclerView)findViewById(R.id.users_list);        mUsersList.setHasFixedSize(true);        mUsersList.setLayoutManager(new LinearLayoutManager(this));    }    @Override    protected void onStart() {        super.onStart();        FirebaseRecyclerAdapter<Users,UsersViewHolder> firebaseRecyclerAdapter = new FirebaseRecyclerAdapter<Users, UsersViewHolder>(                Users.class,                R.layout.single_user_layout,                UsersViewHolder.class,                mUsersDatabase        ) {            @Override            protected void populateViewHolder(UsersViewHolder viewHolder, Users users, int position) {                viewHolder.setName(users.getFullname());                viewHolder.setStatus(users.getStatus());                viewHolder.setThumbpic(users.getThumbpic(), getApplicationContext());                final String user_id = getRef(position).getKey();                viewHolder.mView.setOnClickListener(new View.OnClickListener() {                    @Override                    public void onClick(View view) {                        Intent profileIntent = new Intent(AllUsers.this, UserProfile.class);                        profileIntent.putExtra("user_id", user_id);                        startActivity(profileIntent);                    }                });            }        };        mUsersList.setAdapter(firebaseRecyclerAdapter);    }    public static class UsersViewHolder extends RecyclerView.ViewHolder{        View mView;        public UsersViewHolder(View itemView) {            super(itemView);            mView=itemView;        }        public void setName(String fullname){            TextView userNameView = mView.findViewById(R.id.user_name);            userNameView.setText(fullname);        }        public void setStatus(String status){            TextView userStatusView = mView.findViewById(R.id.user_status);            userStatusView.setText(status);        }        public void setThumbpic(String thumbpic, Context ctx) {            CircleImageView userImageView = (CircleImageView)mView.findViewById(R.id.thumb_image);            Picasso.with(ctx).load(thumbpic).placeholder(R.drawable.default_profile_pic).into(userImageView);        }    }}