package com.example.somya.savoir;import android.app.ProgressDialog;import android.support.annotation.NonNull;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.support.v7.widget.Toolbar;import android.view.View;import android.widget.Button;import android.widget.TextView;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.auth.FirebaseAuth;import com.google.firebase.auth.FirebaseUser;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;public class ChangeStatus extends AppCompatActivity {    private Toolbar mToolbar;    private TextView mStatus;    private Button mSaveButton;    private DatabaseReference mStatusDatabase;    private FirebaseUser mCurrentUser;    private ProgressDialog mProgressDialog;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_change_status);        mCurrentUser = FirebaseAuth.getInstance().getCurrentUser();        String uid = mCurrentUser.getUid();        mStatusDatabase = FirebaseDatabase.getInstance().getReference().child("Users").child(uid);        mToolbar = (Toolbar) findViewById(R.id.status_page_toolbar);        setSupportActionBar(mToolbar);        getSupportActionBar().setTitle("Change Status");        getSupportActionBar().setDisplayHomeAsUpEnabled(true);        mStatus = (TextView)findViewById(R.id.status_input);        mSaveButton = (Button)findViewById(R.id.save_status_button);        mSaveButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                mProgressDialog = new ProgressDialog(ChangeStatus.this);                mProgressDialog.setTitle("Saving Changes");                mProgressDialog.setMessage("Please wait");                String status = mStatus.getText().toString().trim();                mStatusDatabase.child("status").setValue(status).addOnCompleteListener(new OnCompleteListener<Void>() {                    @Override                    public void onComplete(@NonNull Task<Void> task) {                        if (task.isSuccessful()){                            mProgressDialog.dismiss();                            finish();                        }                        else{                            Toast.makeText(getApplicationContext(),"Error Occurred",Toast.LENGTH_LONG).show();                        }                    }                });            }        });    }}