package com.example.somya.savoir;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.widget.Button;import android.widget.ImageView;import android.widget.TextView;import com.google.firebase.database.DataSnapshot;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.google.firebase.database.ValueEventListener;import com.squareup.picasso.Picasso;public class UserProfile extends AppCompatActivity {    private ImageView mProfileImage;    private TextView mProfileName, mProfileStatus, mProfileFriendsCount;    private Button mProfileSendReqBtn;    private DatabaseReference mUsersDatabase;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_user_profile);        String user_id = getIntent().getStringExtra("user_id");        mUsersDatabase = FirebaseDatabase.getInstance().getReference().child("Users").child(user_id);        mProfileImage = (ImageView)findViewById(R.id.profile_DisplayPicture);        mProfileName = (TextView)findViewById(R.id.profile_DisplayName);        mProfileStatus = (TextView)findViewById(R.id.profile_DisplayStatus);        mProfileFriendsCount = (TextView)findViewById(R.id.profile_FriendCount);        mProfileSendReqBtn = (Button)findViewById(R.id.profile_SendReqBtn);        mUsersDatabase.addValueEventListener(new ValueEventListener() {            @Override            public void onDataChange(DataSnapshot dataSnapshot) {                String display_name = dataSnapshot.child("fullname").getValue().toString();                String display_status = dataSnapshot.child("status").getValue().toString();                String display_image = dataSnapshot.child("profilepic").getValue().toString();                mProfileName.setText(display_name);                mProfileStatus.setText(display_status);                Picasso.with(UserProfile.this).load(display_image).placeholder(R.drawable.default_profile_pic).into(mProfileImage);            }            @Override            public void onCancelled(DatabaseError databaseError) {            }        });    }}