package com.example.somya.savoir;import android.net.Uri;/** * Created by somya on 8/13/2017. */public class Users {    public String fullname;    public String profilepic;    public String status;    public String thumbpic;    public Users(){    }    public Users(String fullname, String profilepic, String status, String thumbpic) {        this.fullname = fullname;        this.profilepic = profilepic;        this.status = status;        this.thumbpic = thumbpic;    }    public String getFullname() {        return fullname;    }    public void setFullname(String fullname) {        this.fullname = fullname;    }    public String getProfilepic() {        return profilepic;    }    public void setProfilepic(String profilepic) {        this.profilepic = profilepic;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getThumbpic() {        return thumbpic;    }    public void setThumbpic(String thumbpic) {        this.thumbpic = thumbpic;    }}